    csvHeaders = netsuiteOrderMap[0].keySet().collect { it as String }
    validFileCsvHelper.setHeaders(csvHeaders)
    invalidFileCsvHelper.setHeaders(csvHeaders)
}

// Check for required fields and separate valid/invalid orders
netsuiteOrderMap.each { record ->
    def orderedRecord = csvHeaders.collect { key -> record[key] != null ? record[key] : "" }
    def missingFields = requiredFields.findAll { !record[it] || record[it].toString().trim() == '' }

    if (missingFields) {
        // Add to invalid CSV with error message
        orderedRecord.add("Missing required fields: ${missingFields.join(', ')}")
        invalidFileCsvHelper.writeToFile(orderedRecord)
        isReqFieldMissFileEmpty = false
    } else {
        validFileCsvHelper.writeToFile(orderedRecord)
        isFileEmpty = false
    }
}
